// import logo from './logo.svg';
import './App.css';
import { useState, useEffect } from 'react';
import Web3 from 'web3';
import newPetrolContract from ".//blockchain/petrolContract";

function App() {

  const [web3,setWeb3] = useState(null);
  const [address, setAddress] = useState(null);
  const [petrolContract, setPetrolContract] = useState(null);
  const [inventory, setInventory] = useState(null);

  const getInventory = async() => {
    const inventory = await petrolContract.methods.checkBalance().call();
    setInventory(inventory);
  };

  const getMyPetrol = async() => {

  };

  useEffect(() => {
    if (petrolContract) getInventory();
  }, [petrolContract, address]);

  const connectWallet = async() => {
    // 请求链接钱包
    const account = await window.ethereum.request({
      method:'eth_requestAccounts'
    });
    // 抓取web3文件
    const web3 = new Web3(window.ethereum);
    setWeb3(web3);
    // 抓取钱包账号地址
    const accounts = await web3.eth.getAccounts();
    console.log(accounts[0]);
    setAddress(accounts[0]);

    // 抓取智能合约
    const pt = newPetrolContract(web3);
    setPetrolContract(pt);
  };

  return (
    <div className="App">
      <div></div>
      <h1>Petrol Exchange-石油交易系统DAPP</h1>
      <button onClick={connectWallet}>connect wallet</button>
      <h3>地址-Address:{address}</h3>
      <section>
        <div>
          <p>库存 Inventory:{inventory}</p>
        </div>
      </section>
      <section>
        <div>
          <p>我的汽油数量 Account:{getMyPetrol}</p>
        </div>
      </section>
      <section>
        <div>
          <input type='type'/>
          <button>BUY</button>
        </div>
      </section>
    </div>
  );
}

export default App;
