// import logo from './logo.svg';
import './App.css';

import { useState, useEffect } from 'react';
import Web3 from 'web3';
import newPetrolContract from ".//blockchain/petrolContract";

function App() {

  const [web3,setWeb3] = useState(null);
  const [address, setAddress] = useState(null);
  const [petrolContract, setPetrolContract] = useState(null);
  const [myDeposit, setMyDeposit] = useState(null);
  const [buyCount, setBuyCount] = useState();



  const getMyDeposit = async() => {
    const depositCount = await petrolContract.methods.myDeposit().call({
      from : address
    });
    console.log("myDeposit is :", depositCount);
    setMyDeposit(""+depositCount);
  };

  const newNumber = (event) => {
    setBuyCount(event.target.value);
  };

  const deposit = async() => {
    await petrolContract.methods.deposit(buyCount).send({
      from : address,
      value : buyCount
    });
  };

  const withdraw = async() => {
    await petrolContract.methods.withdraw().send({
      from : address
    });

  };
  const connectWallet = async() => {
    // 请求链接钱包
    const account = await window.ethereum.request({
      method:'eth_requestAccounts'
    });
    // 抓取web3文件
    const web3 = new Web3(window.ethereum);
    setWeb3(web3);
    // 抓取钱包账号地址
    const accounts = await web3.eth.getAccounts();
    console.log(accounts[0]);
    setAddress(accounts[0]);

    // 抓取智能合约
    const pt = newPetrolContract(web3);
    setPetrolContract(pt);
  };

  return (
    <div class="body-class">
      <img class="full-bg"></img>

      <div class="block full2">
        <div class="tab-content">
        <div class="tab-pane active" id="shop">
          <div class="shuaibi-tip animated tada text-center"><i class="fa fa-heart text-danger"></i> <b>Sir在线生成短链接！</b></div>
          <div class="form-group">
            <div class="input-group">
              <div class="input-group-addon">短链类型</div>
              <select name="dwz-type" id="type" class="form-control">
                <option value="2rul">2rul</option><option value="6fg">6fg</option>              </select>
            </div>
          </div>
          <div class="form-group">
            <div class="input-group">
              <div class="input-group-addon">缩短网址</div>
              <input type="url" name="url" id="url" value="" class="form-control" placeholder="请输入正确网址" required=""></input>
            </div>
          </div>
          <button type="submit" class="am-btn am-btn-success am-btn-sm am-btn-block am-round" id="start"onclick="sub()">一键生成</button>
          <br/>
          <div id="result2" class="form-group" >
            <tbody id="list">
            </tbody>
          </div>
          <div class="am-modal-bd">
            <p id="dwz"></p>
            <p id="data"></p>
            <p id="tzurl"></p>
            <p id="qrcode"></p>
          </div>
        </div>
        </div>
      </div>


    <div className="App">
      <div></div>
      <h1>银行DAPP</h1>
      <button onClick={connectWallet}>connect wallet</button>
      <h3>账户地址-Address:{address}</h3>


      <section>
        <div>
          <p>银行余额:{myDeposit} <button onClick={getMyDeposit}>查询</button> </p>


        </div>
      </section>

      <section>
        <div>
          <input onChange={newNumber} type='type'/>
          <button onClick={deposit}>存钱</button>
        </div>
      </section>
      <section>
        <div>
        <button onClick={withdraw}>取钱</button>
        </div>
      </section>
    </div>
    </div>
  );
}

export default App;
